    // Make sure the weapon is the laser trip mine and the player is valid
    if (equali(name, "weapon_tripmine") && client > 0 && client <= g_MaxClients)
    {
        // Create a new beam if one does not already exist for this player
        if (g_LaserTripFadeIdx[client] == 0)
