            new Float:org[3], Float:end[3];

            // Get the player's current position and the direction they are facing
            get_user_origin(client, org);
            get_user_forward(client, end);

            // Create the beam with the initial alpha value and width
            g_LaserTripFadeIdx[client] = te_beamcreate(org, end, "sprites/laserbeam.spr", 0, 0, TRIPMINE_RANGE, 0, get_pcvar_float(p_lasertrip_beam_width), get_pcvar_float(p_lasertrip_beam_alpha), 0, get_pcvar_float(p_lasertrip_beam_duration));
        }
        // Set a timer to start updating the beam's color and width values
        SetTimerEx("UpdateLaserTripFadeBeam", BEAM_FADE_DELAY, false, "i", client);
    }
}
