// 定义游戏相关的变量
let ROW = 10; // 行数
let COL = 10; // 列数
let BOMB_COUNT = 10; // 雷数
let board = []; // 二维数组表示棋盘

// 初始化棋盘
function initBoard() {
  // 初始化棋盘
