            showCell(i, j);
          }
        }
      }
    }
  }

  // 判断游戏是否胜利
  if (checkWin()) {
    gameWin();
  }
}

// 标记旗子
function flagCell(row, col) {
  let cell = board[row][col];
  if (cell.isShow) {
    return; // 如果格子已经显示过，则不操作
  }

  cell.isFlag = !cell.isFlag;
  updateBoard(row, col, cell.isFlag ? -1 : cell.bombCount);

  // 判断游戏是否胜利
