  // 显示所有雷的位置
  for (let i = 0; i < ROW; i++) {
    for (let j = 0; j < COL; j++) {
      if (board[i][j].isBomb) {
        updateBoard(i, j, "雷");
      }
    }
  }

  alert("游戏结束！");
