```javascript
const canvas = document.createElement("canvas");
canvas.width = 800;
canvas.height = 400;
document.body.appendChild(canvas);
const ctx = canvas.getContext("2d");

const player = { x: 50, y: 200, size: 20, dx: 0, dy: 0 };
const obstacles = [];
let spawnTimer = 0;

function update() {
  player.dx *= 0.9;
  player.dy += 0.5;
  player.x += player.dx;
  player.y += player.dy;

  if (player.y > canvas.height - player.size) {
    player.y = canvas.height - player.size;
    player.dy = 0;
  }

  spawnTimer++;
  if (spawnTimer % 120 === 0) {
    const obstacle = {
      x: canvas.width,
      y: Math.random() * (canvas.height - 100),
      width: player.size * (1 + Math.random() * 2),
      height: player.size * (2 + Math.random() * 4)
    };
    obstacles.push(obstacle);
  }

  for (let i = 0; i < obstacles.length; i++) {
    const o = obstacles[i];
    o.x -= 2;

    if (
      player.x + player.size > o.x &&
      player.y + player.size > o.y &&
      player.x < o.x + o.width &&
      player.y < o.y + o.height
    ) {
      player.dy *= -1;
    }
    if (o.x + o.width < 0) {
      obstacles.shift();
    }
  }
}

function draw() {
  ctx.clearRect(0, 0, canvas.width, canvas.height);

  ctx.fillStyle = "blue";
  ctx.beginPath();
  ctx.arc(player.x, player.y, player.size, 0, Math.PI * 2);
  ctx.fill();

  ctx.fillStyle = "black";
  for (const o of obstacles) {
    ctx.fillRect(o.x, o.y, o.width, o.height);
  }
}

function loop() {
  update();
  draw();
  requestAnimationFrame(loop);
}

loop();

document.addEventListener("keydown", (e) => {
  if (e.key === "w" && player.y === canvas.height - player.size) {
    player.dy = -10;
  } else if (e.key === "a") {
    player.dx = -5;
  } else if (e.key === "d") {
    player.dx = 5;
  }
});
