Для отрисовки объектов с использованием GLSL шейдера нам нужно воспользоваться WebGL API вместо Canvas 2D API. Вот код с GLSL шейдерами, использующими WebGL для отрисовки игровых объектов:

```html
<!DOCTYPE html>
<html>
  <head>
    <style>
      body { margin: 0; }
    </style>
  </head>
  <body>
    <canvas id="canvas"></canvas>
    <script>
      const canvas = document.getElementById("canvas");
      canvas.width = 800;
      canvas.height = 400;

      const gl = canvas.getContext("webgl");

      const vertShaderSrc = `
        attribute vec4 a_position;

        void main() {
          gl_Position = a_position;
        }
      `;
      const fragShaderSrc = `
        precision mediump float;

        uniform vec4 u_color;

        void main() {
          gl_FragColor = u_color;
        }
      `;

      function createShader(gl, type, src) {
        const shader = gl.createShader(type);
        gl.shaderSource(shader, src);
        gl.compileShader(shader);
        return shader;
      }

      function createProgram(gl, vertShader, fragShader) {
        const program = gl.createProgram();
        gl.attachShader(program, vertShader);
        gl.attachShader(program, fragShader);
        gl.linkProgram(program);
        return program;
      }

      const vertShader = createShader(gl, gl.VERTEX_SHADER, vertShaderSrc);
      const fragShader = createShader(gl, gl.FRAGMENT_SHADER, fragShaderSrc);
      const program = createProgram(gl, vertShader, fragShader);

      const positionLoc = gl.getAttribLocation(program, "a_position");
      const colorLoc = gl.getUniformLocation(program, "u_color");

      const positionBuffer = gl.createBuffer();
      gl.bindBuffer(gl.ARRAY_BUFFER, positionBuffer);
      gl.enableVertexAttribArray(positionLoc);
      gl.vertexAttribPointer(positionLoc, 2, gl.FLOAT, false, 0, 0);

      gl.useProgram(program);

      const player = { x: 50, y: 200, size: 20, dx: 0, dy: 0 };
      const obstacles = [];
      let spawnTimer = 0;

      function update() {
        player.dx *= 0.9;
        player.dy += 0.5;
        player.x += player.dx;
        player.y += player.dy;

        if (player.y > canvas.height - player.size) {
          player.y = canvas.height - player.size;
          player.dy = 0;
        }

        spawnTimer++;
        if (spawnTimer % 120 === 0) {
          const obstacle = {
            x: canvas.width,
            y: Math.random() * (canvas.height - 100),
            width: player.size * (1 + Math.random() * 2),
            height: player.size * (2 + Math.random() * 4)
          };
          obstacles.push(obstacle);
        }

        for (let i = 0; i < obstacles.length; i++) {
          const o = obstacles[i];
          o.x -= 2;

          if (
            player.x + player.size > o.x &&
            player.y + player.size > o.y &&
            player.x < o.x + o.width &&
            player.y < o.y + o.height
          ) {
            player.dy *= -1;
          }
          if (o.x + o.width < 0) {
            obstacles.shift();
          }
        }
      }

      function drawRect(x, y, width, height, color) {
        const left = (x / canvas.width) * 2 - 1;
        const top = -((y / canvas.height) * 2 - 1);
        const right = ((x + width) / canvas.width) * 2 - 1;
        const bottom = -(((y + height) / canvas.height) * 2 - 1);

        const positions = [
          left, top,
          right, top,
          left, bottom,
          left, bottom,
          right, top,
          right, bottom
        ];

        gl.bufferData(gl.ARRAY_BUFFER, new Float32Array(positions), gl.STATIC_DRAW);
        gl.uniform4fv(colorLoc, color);
        gl.drawArrays(gl.TRIANGLES, 0, 6);
      }

      function draw() {
        gl.viewport(0, 0, gl.canvas.width, gl.canvas.height);
        gl.clearColor(0, 0, 0, 1);
        gl.clear(gl.COLOR_BUFFER_BIT);

        drawRect(player.x - player.size / 2, player.y - player.size / 2, player.size, player.size, [0, 0, 1, 1]);

        for (const o of obstacles) {
          drawRect(o.x, o.y, o.width, o.height, [0, 0, 0, 1]);
        }
      }

      function loop() {
        update();
        draw();
        requestAnimationFrame(loop);
      }

      loop();

      document.addEventListener("keydown", (e) => {
        if (e.key === "w" && player.y === canvas.height - player.size) {
          player.dy = -10;
        } else if (e.key === "a") {
          player.dx = -5;
        } else if (e.key === "d") {
          player.dx = 5;
        }
      });
    </script>
  </body>
</html>
