Здесь простой пример 2D платформера на JavaScript с использованием элемента canvas для рисования и управления с помощью клавиш W, A, D:

```javascript
// Создание canvas элемента
const canvas = document.createElement('canvas');
const context = canvas.getContext('2d');
canvas.width = 640;
canvas.height = 480;
document.body.appendChild(canvas);

const player = {
  x: 100,
  y: 420,
  width: 50,
  height: 50,
  velocityX: 0,
  velocityY: 0,
  onGround: false,
};

const platform = {
  x: 200,
  y: 300,
  width: 200,
  height: 20,
};

function update() {
  player.velocityY += 1;

  player.x += player.velocityX;
  player.y += player.velocityY;

  if (player.x < 0) player.x = 0;
  if (player.y < 0) player.y = 0;
  if (player.x + player.width > canvas.width) player.x = canvas.width - player.width;
  if (player.y + player.height > canvas.height) {
    player.y = canvas.height - player.height;
    player.onGround = true;
  } else {
    player.onGround = false;
  }

  if (player.x + player.width > platform.x && player.x < platform.x + platform.width &&
      player.y + player.height > platform.y && player.y < platform.y + platform.height) {
    player.y = platform.y - player.height;
    player.onGround = true;
  }

  if (player.onGround) {
    player.velocityY = 0;
  }
}

function draw() {
  context.clearRect(0, 0, canvas.width, canvas.height);

  context.fillStyle = 'blue';
  context.fillRect(player.x, player.y, player.width, player.height);

  context.fillStyle = 'green';
  context.fillRect(platform.x, platform.y, platform.width, platform.height);
}

function gameLoop() {
  update();
  draw();
  requestAnimationFrame(gameLoop);
}

gameLoop();

document.addEventListener('keydown', (e) => {
  if (e.key === 'w' && player.onGround) {
    player.velocityY = -20;
  }
  if (e.key === 'a') {
    player.velocityX = -5;
  }
  if (e.key === 'd') {
    player.velocityX = 5;
  }
});

document.addEventListener('keyup', (e) => {
  if (e.key === 'a' || e.key === 'd') {
    player.velocityX = 0;
  }
});
