const platforms = [];

function random(min, max) {
  return Math.floor(Math.random() * (max - min + 1)) + min;
}

function addPlatform() {
  const platform = {
    x: canvas.width,
    y: random(50, canvas.height - 30),
    width: 200,
    height: random(player.height, 100),
    velocityX: -3,
  };
  platforms.push(platform);
}

// Запланировать добавление первой платформы через 2 секунды
setTimeout(function addAndSchedule() {
  addPlatform();
  // Запланировать добавление следующей платформы через 2 секунды
  setTimeout(addAndSchedule, 2000);
}, 2000);

function update() {
  player.velocityY += 0.5;

  player.y += player.velocityY;

  if (player.y + player.height > canvas.height) {
    player.onGround = true;
    player.velocityY *= -0.5;
  } else {
    player.onGround = false;
  }

  platforms.forEach((platform, index) => {
    platform.x += platform.velocityX;

    if (platform.x + platform.width < 0) {
      platforms.splice(index, 1);
      return;
    }

    if (player.x + player.width > platform.x &&
        player.x < platform.x + platform.width &&
        player.y + player.height > platform.y &&
        player.y < platform.y + platform.height
    ) {
      player.y = platform.y - player.height;
      player.onGround = true;
      player.velocityY *= -0.5;
    }
  });

  // Плавное движение игрока по оси X
  player.x += player.velocityX;
  if (player.x < 0) player.x = 0;
  if (player.x + player.width > canvas.width) player.x = canvas.width - player.width;
}

function draw() {
  context.clearRect(0, 0, canvas.width, canvas.height);

  context.fillStyle = 'blue';
  context.fillRect(player.x, player.y, player.width, player.height);

  context.fillStyle = 'green';
  platforms.forEach((platform) => {
    context.fillRect(platform.x, platform.y, platform.width, platform.height);
  });
}

function gameLoop() {
  update();
  draw();
  requestAnimationFrame(gameLoop);
}

gameLoop();

document.addEventListener('keydown', (e) => {
  if (e.key === 'w' && player.onGround) {
