      context.fillStyle = 'rgba(0, 0, 0, 0.8)';
      context.fillRect(0, 0, canvas.width, canvas.height);

      const boxWidth = 100;
      const boxHeight = 70;

      context.fillStyle = 'white';
      context.fillRect(canvas.width / 2 - boxWidth / 2, canvas.height / 2 - boxHeight / 2, boxWidth, boxHeight);
    }

    function onMouseMove(event) {
      const rect = canvas.getBoundingClientRect();

      mouseX = event.clientX - rect.left;
      mouseY = event.clientY - rect.top;
    }

    function onClick(event) {
      if (!gameOver) {
        return;
      }

      const boxWidth = 100;
      const boxHeight = 70;

      const boxLeft = canvas.width / 2 - boxWidth / 2;
      const boxRight = canvas.width / 2 + boxWidth / 2;
      const boxTop = canvas.height / 2 - boxHeight / 2;
      const boxBottom = canvas.height / 2 + boxHeight / 2;

      if (
        mouseX >= boxLeft &&
        mouseX <= boxRight &&
        mouseY >= boxTop &&
        mouseY <= boxBottom
      ) {
        resetGame();
      }
    }

    canvas.addEventListener('mousemove', onMouseMove);
    canvas.addEventListener('click', onClick);

    resetGame();
    gameLoop();
