                    y -= 5;
                    break;
                case 'KeyA':
                    x -= 5;
                    break;
                case 'KeyS':
                    y += 5;
                    break;
                case 'KeyD':
                    x += 5;
                    break;
                case 'Space':
                    drawing = !drawing;
