            [0, 0, 0, 0, 0, 0, 0, "O", 0, 0, 0, "O", 0],
            [0, 0, 0, "O", 0, 0, 0, 0, 0, "X", "X", 0, 0],
            [0, 0, "O", 0, 0, 0, 0, "X", 0, 0, 0, "X", 0],
            [0, "O", 0, 0, 0, 0, "X", 0, 0, 0, "X", 0, 0],
            ["X", 0, 0, 0, 0, "O", 0, 0, "O", 0, 0, 0, "X"]
        ]
        self.bar = {"X": 0, "O": 0}
        self.off = {"X": 0, "O": 0}
        self.current_player = "X"
        self.dice = [0, 0]

    def roll_dice(self):
        self.dice = [randint(1, 6), randint(1, 6)]

    def get_valid_moves(self, position):
        # Return a list of valid moves for a given position
        valid_moves = []
        if self.bar[self.current_player] > 0:
            # Handle moves from the bar
            for i in range(len(self.board)):
                if self.current_player == "X":
                    if self.board[i][position - 1] == 0 or self.board[i][position - 1] == "X":
                        valid_moves.append((i, position))
                else:
                    if self.board[i][24 - position] == 0 or self.board[i][24 - position] == "O":
                        valid_moves.append((i, 24 - position))
        else:
            # Handle normal moves
            for i in range(len(self.board)):
                if self.current_player == "X":
                    if self.board[i][position - 1] == "X":
                        for j in range(1, self.dice[0] + 1):
                            if i + j < len(self.board) and (self.board[i + j][position - 1] == 0 or self.board[i + j][position - 1] == "X"):
                                valid_moves.append((i, position, i + j, position))
                        for j in range(1, self.dice[1] + 1):
                            if i + j < len(self.board) and (self.board[i + j][position - 1] == 0 or self.board[i + j][position - 1] == "X"):
                                valid_moves.append((i, position, i + j, position))
                    elif self.board[i][position - 1] != 0:
                        continue
                else:
                    if self.board[i][24 - position] == "O":
                        for j in range(1, self.dice[0] + 1):
                            if i - j >= 0 and (self.board[i - j][24 - position] == 0 or self.board[i - j][24 - position] == "O"):
                                valid_moves.append((i, 24 - position, i - j, 24 - position))
                        for j in range(1, self.dice[1] + 1):
                            if i - j >= 0 and (self.board[i - j][24 - position] == 0 or self.board[i - j][24 - position] == "O"):
                                valid_moves.append((i, 24 - position, i - j, 24 - position))
                    elif self.board[i][24 - position] != 0:
                        continue
        return valid_moves

    def make_move(self, move):
        if move[0] == "bar":
            if self.current_player == "X":
                self.board[move[1]][move[2] - 1] = "X"
            else:
                self.board[move[1]][24 - move[2]] = "O"
            self.bar[self.current_player] -= 1
        else:
            if self.current_player == "X":
                self.board[move[0]][move[1] - 1] = 0
                if self.board[move[2]][move[3] - 1] == "O":
                    self.bar["O"] += 1
                    self.board[move[2]][move[3] - 1] = "X"
                else:
                    self.board[move[2]][move[3] - 1] = "X"
            else:
                self.board[move[0]][24 - move[1]] = 0
                if self.board[move[2]][24 - move[3]] == "X":
                    self.bar["X"] += 1
                    self.board[move[2]][24 - move[3]] = "O"
                else:
                    self.board[move[2]][24 - move[3]] = "O"

    def check_win(self):
        if self.off[self.current_player] == 15:
            print(f"{self.current_player} wins!")
            return True
        return False

    def play_game(self):
        while True:
            self.roll_dice()
            print(f"{self.current_player} rolls {self.dice[0]}, {self.dice[1]}")
            valid_moves = []
            for i in range(1, 25):
                valid_moves += self.get_valid_moves(i)
            if valid_moves == []:
                print(f"{self.current_player} can't move, skipping turn")
                self.current_player = "X" if self.current_player == "O" else "O"
                continue
            while True:
                print(f"Valid moves for {self.current_player}: {valid_moves}")
                move = input("Enter move: ")
                if move == "quit":
                    return
                if not move.isdigit():
                    print("Invalid input, try again")
                    continue
                move = int(move)
                if move < 1 or move > len(valid_moves):
                    print("Invalid input, try again")
                    continue
                self.make_move(valid_moves[move - 1])
                self.check_win()
                self.current_player = "X" if self.current_player == "O" else "O"
                break

