import random
import os

def clear_screen():
    os.system('cls' if os.name == 'nt' else 'clear')

def print_board(board):
    print("Score: ", score)
    for row in board:
        print(row)

def add_tile(board):
    while True:
        x = random.randint(0, 3)
        y = random.randint(0, 3)
        if board[x][y] == 0:
            board[x][y] = 2
            break

def check_game_over(board):
    # Check for empty tile
    for row in board:
        if 0 in row:
            return False
    # Check horizontal and vertical adjacents
    for i in range(3):
        for j in range(3):
            if board[i][j] == board[i][j + 1] or board[i][j] == board[i + 1][j]:
                return False
    # Check bottom and right edges
    if board[3][0] == board[3][1] or board[3][1] == board[3][2] or board[3][2] == board[3][3]:
        return False
    if board[0][3] == board[1][3] or board[1][3] == board[2][3] or board[2][3] == board[3][3]:
        return False
    return True

def merge_left(row):
    for i in range(3):
        if row[i] == row[i + 1]:
            row[i] *= 2
            global score
            score += row[i]
            row[i + 1] = 0

def merge_right(row):
    for i in range(3, 0, -1):
        if row[i] == row[i - 1]:
            row[i] *= 2
            global score
            score += row[i]
            row[i - 1] = 0

def shift_left(board):
    for row in board:
        while True:
            try:
                row.remove(0)
            except ValueError:
                break
        row += [0] * (4 - len(row))
        merge_left(row)

def shift_right(board):
    for row in board:
        while True:
            try:
                row.remove(0)
            except ValueError:
                break
        row = [0] * (4 - len(row)) + row[::-1]
        merge_right(row)
        row = row[::-1]

def shift_up(board):
    for j in range(4):
        col = [board[i][j] for i in range(4)]
        while True:
            try:
                col.remove(0)
            except ValueError:
                break
        col += [0] * (4 - len(col))
        merge_left(col)
        for i in range(4):
            board[i][j] = col[i]

def shift_down(board):
    for j in range(4):
        col = [board[i][j] for i in range(4)]
        while True:
            try:
                col.remove(0)
            except ValueError:
                break
        col = [0] * (4 - len(col)) + col[::-1]
        merge_right(col)
        col = col[::-1]
        for i in range(4):
            board[i][j] = col[i]

def play_game():
    global score
    board = [[0] * 4 for i in range(4)]
    add_tile(board)
    add_tile(board)
    print_board(board)
    while True:
        move = input("Use arrow keys (left, right, up, down) to move tiles. Press Q to quit. ")
        clear_screen()
        if move.lower() == 'q':
            print("Game over.")
            break
        elif move.lower() == 'left':
            shift_left(board)
        elif move.lower() == 'right':
            shift_right(board)
        elif move.lower() == 'up':
            shift_up(board)
        elif move.lower() == 'down':
            shift_down(board)
        else:
            print("Invalid move. Use arrow keys to move tiles. Press Q to quit.")
            continue
        add_tile(board)
        print_board(board)
        if check_game_over(board):
            print("Game over.")
            break

score = 0
play_game()
