  if (board[row][col] !== '') {
    console.log('This move has already been made!');
    return;
  }
  board[row][col] = currentPlayer;
  printBoard();
  if (checkWin()) {
    console.log('Player ' + currentPlayer + ' wins!');
    gameEnd = true;
    return;
  }
  if (currentPlayer === 'X') {
    currentPlayer = 'O';
  } else {
    currentPlayer = 'X';
  }
}

