JavaScript (main.js):
```javascript
const canvas = document.getElementById("gameCanvas");
const ctx = canvas.getContext("2d");
canvas.width = 800;
canvas.height = 400;

const paddleWidth = 10;
const paddleHeight = 100;
const ballSize = 10;

let leftPaddleY = (canvas.height / 2) - (paddleHeight / 2);
let rightPaddleY = leftPaddleY;
let ballX = canvas.width / 2;
let ballY = canvas.height / 2;
let ballSpeedX = 5;
let ballSpeedY = 2;
const AI_SPEED = 3;

function drawElements() {
  ctx.fillStyle = "white";

  ctx.fillRect(0, leftPaddleY, paddleWidth, paddleHeight);
  ctx.fillRect(canvas.width - paddleWidth, rightPaddleY, paddleWidth, paddleHeight);
  ctx.fillRect(ballX, ballY, ballSize, ballSize);
}

function moveBall() {
  ballX += ballSpeedX;
  ballY += ballSpeedY;

  if (ballY < 0 || ballY + ballSize > canvas.height) {
    ballSpeedY = -ballSpeedY;
  }

  if (ballX < 0) {
    if (ballY + ballSize > leftPaddleY && ballY < leftPaddleY + paddleHeight) {
      ballSpeedX = -ballSpeedX;
    } else {
      // right player scores
      resetBall();
    }
  } else if (ballX + ballSize > canvas.width) {
    if (ballY + ballSize > rightPaddleY && ballY < rightPaddleY + paddleHeight) {
      ballSpeedX = -ballSpeedX;
    } else {
      // left player scores
      resetBall();
    }
  }
}

function resetBall() {
  ballX = canvas.width / 2;
  ballY = canvas.height / 2;
  ballSpeedX = -ballSpeedX;
}

function moveAI() {
  const paddleCenter = rightPaddleY + paddleHeight / 2;
  if (paddleCenter < ballY - 35) {
    rightPaddleY += AI_SPEED;
  } else if (paddleCenter > ballY + 35) {
    rightPaddleY -= AI_SPEED;
  }
}

function handleKeyPress(event) {
  const PADDLE_SPEED = 20;
  if (event.key === "w" && leftPaddleY > 0) {
    leftPaddleY -= PADDLE_SPEED;
  }
  if (event.key === "s" && leftPaddleY < canvas.height - paddleHeight) {
    leftPaddleY += PADDLE_SPEED;
  }
}

document.addEventListener("keydown", handleKeyPress);

function gameLoop() {
  ctx.clearRect(0, 0, canvas.width, canvas.height);

  drawElements();
  moveBall();
  moveAI();

  requestAnimationFrame(gameLoop);
}

gameLoop();
```

