    def __init__(self):
        self.state = "off"

    def turn_on(self):
        if self.state == "off":
            self.state = "on"

    def turn_off(self):
        if self.state == "on":
            self.state = "off"

# 使用状态模式
class Light:
    def __init__(self, state):
        self.state = state

    def turn_on(self):
        self.state.turn_on()

    def turn_off(self):
        self.state.turn_off()

class State:
    def turn_on(self):
        raise NotImplementedError

    def turn_off(self):
        raise NotImplementedError

class OffState(State):
    def turn_on(self):
        return OnState()

    def turn_off(self):
        return self

class OnState(State):
    def turn_on(self):
        return self

    def turn_off(self):
        return OffState()
```
