// Класс для работы с блоком uni:energy_node
public class EnergyNode {
  private int energy;

  public EnergyNode(int energy) {
    this.energy = energy;
  }

  public int getEnergy() {
    return energy;
  }

  public void setEnergy(int energy) {
    this.energy = energy;
  }

  // Прибавление и вычитание энергии между блоками
  public void transmitEnergy(EnergyNode target) {
    int transferAmount = (int) (Math.random() * 10); // Рандомное количество передаваемой энергии

    if (energy >= transferAmount) {
      energy -= transferAmount;
      target.setEnergy(target.getEnergy() + transferAmount);
    }
  }

  // Метод для выборки блоков и передачи энергии между ними
  public void transmitEnergyToRandomBlock(World world, BlockPos pos) {
    List<BlockPos> blockList = new ArrayList<>();
    blockList.add(pos);
    blockList.addAll(findBlocks(world, pos, "uni:energy_node"));
    blockList.addAll(findBlocks(world, pos, "uni:small_battery"));
    blockList.addAll(findBlocks(world, pos, "uni:component_battery"));

    BlockPos targetPos = blockList.get((int) (Math.random() * blockList.size()));
    BlockState targetState = world.getBlockState(targetPos);
    Block targetBlock = targetState.getBlock();

    if (targetBlock instanceof EnergyNode) {
      EnergyNode targetNode = (EnergyNode) targetBlock;
      transmitEnergy(targetNode);
    }
  }

  // Поиск блоков с заданным именем
  public List<BlockPos> findBlocks(World world, BlockPos pos, String blockName) {
    List<BlockPos> blockList = new ArrayList<>();
    Block targetBlock = Registry.BLOCK.get(new Identifier(blockName));

    for (BlockPos candidatePos : BlockPos.iterate(pos.add(-5, -5, -5), pos.add(5, 5, 5))) {
      if (world.getBlockState(candidatePos).getBlock() == targetBlock) {
        blockList.add(candidatePos);
      }
    }

    return blockList;
  }
}

