void UpdateLaserTripFadeBeam(index)
{
    // Get the current end position of the laser trip beam
    new Float:end[3];
    te_getbeamend(g_LaserTripFadeIdx[index], end);

    // Reduce the end position by a small amount to make the beam "fade out"
    end[0] -= BEAM_LENGTH_FADE_STEP;
    end[1] -= BEAM_LENGTH_FADE_STEP;
    end[2] -= BEAM_LENGTH_FADE_STEP;

    // Update the beam with the new end position
    te_beamsetend(g_LaserTripFadeIdx[index], end);

    // If the beam is completely faded out, remove it
    if (end[0] <= 0.0 && end[1] <= 0.0 && end[2] <= 0.0) te_removebeam(g_LaserTripFadeIdx[index]);
    else SetTimerEx("UpdateLaserTripFadeBeam", BEAM_LENGTH_FADE_DELAY, false, "i", index);
}

