using UnityEngine;

public class Player : MonoBehaviour
{
    // 定义玩家的移动速度和步长
    public float moveSpeed = 5f;
    public float stepLength = 1f;

    private bool isMoving = false;
    private Vector3 targetPosition;
    private Vector3 boxPosition;

    void Update()
    {
        // 当角色没有移动时，使用键盘控制它移动
        if (!isMoving)
        {
            float horizontal = Input.GetAxisRaw("Horizontal");
            float vertical = Input.GetAxisRaw("Vertical");

            if (horizontal != 0f)
            {
                targetPosition = transform.position + new Vector3(stepLength * Mathf.Sign(horizontal), 0f, 0f);
                Move();
            }
            else if (vertical != 0f)
            {
                targetPosition = transform.position + new Vector3(0f, 0f, stepLength * Mathf.Sign(vertical));
                Move();
            }
        }
        // 当角色正在移动时，执行移动方法
        else
        {
            Move();
        }
    }

    // 角色移动方法
    void Move()
    {
        transform.position = Vector3.MoveTowards(transform.position, targetPosition, moveSpeed * Time.deltaTime);
        if (transform.position == targetPosition)
        {
            isMoving = false;
            if (boxPosition != Vector3.zero)
            {
                PushBox();
            }
        }
    }

    // 当遇到箱子时，获取箱子并执行推动方法
    void OnTriggerEnter(Collider other)
    {
        if (other.gameObject.CompareTag("Box"))
        {
            boxPosition = other.transform.position + (other.transform.position - transform.position);
        }
    }

    // 箱子推动方法
    void PushBox()
    {
        Collider[] hitColliders = Physics.OverlapSphere(boxPosition, 0.1f);
        if (hitColliders.Length == 0)
        {
            GameObject box = GameObject.FindGameObjectWithTag("Box");
            if (box != null)
            {
                box.transform.position = boxPosition;
            }
        }
    }
}
```

