public class CheminVersRouteConverter {

    private static final int MAX_NOM_CHEMIN_LENGTH = 50;

    public Route convertCheminToRoute(Chemin chemin) {
        if (chemin.getNom().length() > MAX_NOM_CHEMIN_LENGTH) {
            throw new IllegalArgumentException("Le nom du chemin ne doit pas dépasser " + MAX_NOM_CHEMIN_LENGTH + " caractères.");
        }

        Route route = new Route();

        // conversion des attributs de Chemin vers les attributs de Route
        route.setNom(chemin.getNom());
        route.setDescription(chemin.getDescription());
        route.setLongueur(chemin.getLongueur());

        // conversion des points de Chemin en waypoints de Route
        List<Point> points = chemin.getPoints();
        List<Waypoint> waypoints = new ArrayList<>();

        for (Point point : points) {
            Waypoint waypoint = new Waypoint(point.getLatitude(), point.getLongitude());
            waypoints.add(waypoint);
        }

        route.setWaypoints(waypoints);

        return route;
    }
} 

