const TelegramBot = require('node-telegram-bot-api');
const Express = require('express');
const app = Express();
const token = 'YOUR_TELEGRAM_BOT_TOKEN';
const bot = new TelegramBot(token, { polling: true });

app.get('/', function (req, res) {
  res.send('Hello World!');
