});

bot.on('message', (msg) => {
  const chatId = msg.chat.id;

  // send welcome message and game URL
  bot.sendMessage(chatId, `Hello ${msg.from.first_name}!`);
  bot.sendGame(chatId, "My Cool Game", "http://mygamesite.com/coolgame");
});

// Add callback queries handling
bot.on('callback_query', (callbackQuery) => {
  const messageId = callbackQuery.message.message_id;
  const chatId = callbackQuery.message.chat.id;
  const gameUrl = "http://mygamesite.com/coolgame";

  bot.answerCallbackQuery(callbackQuery.id, { url: gameUrl }, false);

  // send instructions to start the game
  bot.sendMessage(chatId, "To start the game, please click the 'Play' button below.", {
    reply_markup: {
