  PShape eye2 = createEye();

  PShape pupil1 = createPupil();
  PShape pupil2 = createPupil();

  eye1.translate(-20, -70, 45); // Positionnement de l'œil gauche
  eye2.translate(20, -70, 45); // Positionnement de l'œil droit
  pupil1.translate(-20, -70, 45);
  pupil2.translate(20, -70, 45); 

  mummy.addChild(eye1); // Ajout de l'œil gauche au corps
  mummy.addChild(eye2); // Ajout de l'œil droit au corps  
  mummy.addChild(pupil1); 
  mummy.addChild(pupil2);


  return mummy;
}


PShape createHead() {
  PShape head = createShape();
  head.beginShape(TRIANGLES);
  head.noStroke();
  head.fill(245, 245, 220); // couleur beige clair
  float rx = 45; // rayon en X
  float ry = 75; // rayon en Y
  float rz = 45; // rayon en Z
  int res = 20; // résolution

  for (int i = 0; i < res; i++) {
    float lat1 = map(i, 0, res, -HALF_PI, HALF_PI);
    float lat2 = map(i + 1, 0, res, -HALF_PI, HALF_PI);

    for (int j = 0; j <= res; j++) {
      float lon = map(j, 0, res, -PI, PI);

      // Calculer les coordonnées pour les deux cercles latéraux
      float x1 = rx * cos(lat1) * sin(lon);
      float y1 = ry * sin(lat1);
      float z1 = rz * cos(lat1) * cos(lon);
      float x2 = rx * cos(lat2) * sin(lon);
      float y2 = ry * sin(lat2);
      float z2 = rz * cos(lat2) * cos(lon);

      // Calculer les coordonnées pour le cercle supérieur
      float x3 = rx * cos(lat1) * sin(lon + TWO_PI/res);
      float y3 = ry * sin(lat1);
      float z3 = rz * cos(lat1) * cos(lon + TWO_PI/res);

      // Calculer les coordonnées pour le cercle inférieur
      float x4 = rx * cos(lat2) * sin(lon + TWO_PI/res);
      float y4 = ry * sin(lat2);
      float z4 = rz * cos(lat2) * cos(lon + TWO_PI/res);

      // Ajouter les triangles pour le cercle latéral
      head.vertex(x1, y1, z1);
      head.vertex(x2, y2, z2);
      head.vertex(x3, y3, z3);
      head.vertex(x3, y3, z3);
      head.vertex(x2, y2, z2);
      head.vertex(x4, y4, z4);

      // Ajouter les triangles pour le haut et le bas de la tête
      if (i == 0) {
        head.vertex(x3, y3, z3);
        head.vertex(x1, y1, z1);
        head.vertex(x4, y4, z4);
      }
      else if (i == res - 1) {
        head.vertex(x2, y2, z2);
        head.vertex(x4, y4, z4);
        head.vertex(x1, y1, z1);
      }

    }
  }
  head.endShape();
  return head;
}


  PShape createArm() {
