PShape momie;  // déclaration de l'objet PShape pour la momie
int mx, my;  // position de départ de la momie
int dx, dy;  // direction de départ de la momie

void setup() {
  size(400, 400);
  momie = loadShape("momie.svg");  // chargement de l'image de la momie
  mx = 200;  // position de départ de la momie au centre de l'écran
  my = 200;
  dx = 0;  // direction de départ de la momie aléatoire
  dy = 0;
  changerDirectionAleatoire();
}

void draw() {
  background(255);
  // dessiner le labyrinthe ici
  // ...
  // dessiner la momie à sa position actuelle
  shape(momie, mx, my);
}

void keypressed() {
  // Déplacer la momie si les touches fléchées sont pressées
  if (keyCode == UP && estCaseLibre(mx, my - 1)) {
    my--;
  } else if (keyCode == DOWN && estCaseLibre(mx, my + 1)) {
    my++;
  } else if (keyCode == LEFT && estCaseLibre(mx - 1, my)) {
    mx--;
  } else if (keyCode == RIGHT && estCaseLibre(mx + 1, my)) {
    mx++;
  }
}

void changerDirectionAleatoire() {
  // Changer aléatoirement la direction de la momie
  int alea = int(random(4));
  if (alea == 0) {
    dx = -1;
    dy = 0;
  } else if (alea == 1) {
    dx = 0;
    dy = -1;
  } else if (alea == 2) {
    dx = 1;
    dy = 0;
  } else if (alea == 3) {
    dx = 0;
    dy = 1;
  }
}

boolean estCaseLibre(int x, int y) {
  // Vérifier si la case (x, y) est libre (pas de mur ni de sortie)
  // à compléter selon la structure de données de votre labyrinthe
  return true;
}

void deplacerMomie() {
  // Déplacer la momie selon sa direction actuelle
  if (estCaseLibre(mx + dx, my + dy)) {
    mx += dx;
    my += dy;
  } else {
    // Si la case suivante n'est pas libre, changer de direction aléatoire
    changerDirectionAleatoire();
  }
}

void mouseClicked() {
  // Lancer le déplacement aléatoire de la momie lorsqu'on clique sur l'écran
  changerDirectionAleatoire();
}

void mouseDragged() {
  // Déplacer manuellement la momie en maintenant le clic gauche de la souris
  mx = mouseX;
  my = mouseY;
}
```

