        r1X = baseRadiusX + ((64-i)/20.0)*(radiusX - baseRadiusX);
        r1Y = baseRadiusY + ((64-i)/20.0)*(radiusY - baseRadiusY);
        r2X = baseRadiusX + ((63-i)/20.0)*(radiusX - baseRadiusX);
        r2Y = baseRadiusY + ((63-i)/20.0)*(radiusY - baseRadiusY);
      } else {
        r1X = radiusX;
        r1Y = radiusY;
        r2X = radiusX;
        r2Y = radiusY;
      }
      float x3 = cos(a) * r1X;
      float z3 = sin(a) * r1Y;
      float x4 = cos(a) * r2X;
      float z4 = sin(a) * r2Y;
      strip.vertex(x3, i*5, z3);
      strip.vertex(x4, (i+1)*5, z4);
      noiseVal += 0.01;
    }
    strip.endShape();
    mummy.addChild(strip);
    angle += TWO_PI/64;
  }


  // Add arms
  PShape arm1 = createArm();
  PShape arm2 = createArm();

  arm1.rotateX(HALF_PI);
  arm2.rotateX(HALF_PI);
  arm1.translate(-30, 20, 0);
  arm2.translate(30, 20, 0);

