#include <stdbool.h>

bool isValidDate(int* nums) {
    int year = nums[0] * 1000 + nums[1] * 100 + nums[2] * 10 + nums[3];
    int month = nums[4] * 10 + nums[5];
    int day = nums[6] * 10 + nums[7];
    if (year != 2023) {
        return false;
    }
    if (month <= 0 || month > 12) {
        return false;
    }
    if (day <= 0 || day > 31) {
        return false;
    }
    if ((month == 4 || month == 6 || month == 9 || month == 11) && day == 31) {
        return false;
    }
    if (month == 2) {
        if (day > 29) {
            return false;
        }
        if (day == 29 && (year % 4 != 0 || (year % 100 == 0 && year % 400 != 0))) {
            return false;
        }
    }
    return true;
}

