    var selection = event.dbIdArray;
    if (selection.length > 0) {
        // 选中构件
        viewer.setThemingColor(selection, new THREE.Color(0xff0000));
        // 设置对应的物体尺寸
        viewer.fitToView(selection);
    }
};
