import { Kortti } from "./kortti.js";

export class Kasi {
  constructor() {
    this.kortit = [];
  }

  static luoKasi() {
    let apukasi = new Kasi();
    return apukasi;
  }

  get kortteja() {
    return this.kortit.length;
  }

  get summa() {
    let summa = 0;
    let aces = 0;
    for (let kortti of this.kortit) {
      if (kortti.arvo === 1) {
        aces++;
      }
      summa += kortti.arvo;
    }
    if (aces && summa <= 11) {
      summa += 10;
    }
    return summa;
  }

  lisaaKortti(kortti) {
    this.kortit.push(kortti);
  }
}

