import { Pakka } from "./moduulit/pakka.js";
import { Kasi } from "./moduulit/kasi.js";
import { Maa } from "./moduulit/maa.js";
import { Kortti } from "./moduulit/kortti.js";

const pelaajankasi = Kasi.luoKasi();
const jakajankasi = Kasi.luoKasi();
const pakka = Pakka.luoPakka();

const pelaajaElementit = document.querySelectorAll("#pelaajan-kortit > .kortti");
const jakajaElementit = document.querySelectorAll("#jakajan-kortit > .kortti");
const tulos = document.getElementById("tulos");

function paivitaKortit(elementit, kortit) {
  for (let i = 0; i < elementit.length; i++) {
    if (kortit[i]) {
      elementit[i].innerHTML = kortit[i].toString();
      if (kortit[i].maa.vari === "punainen") {
        elementit[i].classList.add("punainen");
        elementit[i].classList.remove("musta");
      } else {
        elementit[i].classList.add("musta");
        elementit[i].classList.remove("punainen");
      }
    } else {
      elementit[i].innerHTML = "";
      elementit[i].classList.remove("musta");
      elementit[i].classList.remove("punainen");
    }
  }
}

function paivitaTulos() {
  tulos.innerHTML = `Pelaajan summa: ${pelaajankasi.summa} | Jakajan summa: ${jakajankasi.summa}`;
}

function aloitaPeli() {
  pakka.sekoita();
  pelaajankasi.lisaaKortti(pakka.otaKortti());
  jakajankasi.lisaaKortti(pakka.otaKortti());
  paivitaKortit(pelaajaElementit, pelaajankasi.kortit);
  paivitaKortit(jakajaElementit, [jakajankasi.kortit[0], new Kortti(Maa.RISTI, 0)]);
  paivitaTulos();
}

function jakajaVuoro() {
  let index = 1;
  let interval = setInterval(() => {
    jakajankasi.lisaaKortti(pakka.otaKortti());
    paivitaKortit(jakajaElementit, jakajankasi.kortit);
    if (jakajankasi.summa >= 17) {
      clearInterval(interval);
      if (pelaajankasi.summa > jakajankasi.summa) {
        tulos.innerHTML = "Pelaaja voitti!";
      } else if (pelaajankasi.summa < jakajankasi.summa) {
        tulos.innerHTML = "Jakaja voitti!";
      } else {
        tulos.innerHTML = "Tasapeli!";
      }
    }
    index++;
  }, 500);
}

document.getElementById("ota-kortti").addEventListener("click", () => {
  if (pelaajankasi.kortit.length < 5) {
    pelaajankasi.lisaaKortti(pakka.otaKortti());
    paivitaKortit(pelaajaElementit, pelaajankasi.kortit);
    paivitaTulos();
  } else {
    tulos.innerHTML = "Viidennen kortin nosto ei ole sallittua!";
  }
});

document.getElementById("jaa").addEventListener("click", () => {
  document.getElementById("ota-kortti").disabled = true;
  document.getElementById("jaa").disabled = true;
  paivitaKortit(jakajaElementit, jakajankasi.kortit);
  jakajaVuoro();
});

document.getElementById("nayta-kortit").addEventListener("click", () => {
  paivitaKortit(jakajaElementit, jakajankasi.kortit);
});

document.getElementById("uusi-peli").addEventListener("click", () => {
  pelaajankasi.kortit = [];
  jakajankasi.kortit = [];
  document.getElementById("ota-kortti").disabled = false;
  document.getElementById("jaa").disabled = false;
  aloitaPeli();
});

aloitaPeli();

