export class Kasi extends Pakka {
  constructor() {
    super();
  }

  static luoKasi() {
    let apukasi = new Kasi();
    return apukasi;
  }

  get kortteja() {
    return this.kortit.length;
  }

  get summa() {
    let summa = 0;
    let aces = 0;
    for (let kortti of this.kortit) {
      if (kortti.arvo === 1) {
        aces++;
        summa += 11;
      } else if (kortti.arvo > 10) {
        summa += 10;
      } else {
        summa += kortti.arvo;
      }
    }
    while (summa > 21 && aces) {
      summa -= 10;
      aces--;
    }
    return summa;
  }
}

