};maa.js:/ Tämä moduuli sisältää maiden (pata, risti, hertta, ruutu) määrittelyt. /
/ Ominaisuuksiin viitataan pistenotaatiolla, eli esim. jos muuttujan nimi on maa, niin maa.nimi, maa.symboli tai maa.vari. /

export const Maa={
PATA:Object.freeze({nimi:‘pata’, symboli:‘\u2660’, vari:‘musta’}),
RISTI:Object.freeze({nimi:‘risti’, symboli:‘\u2663’, vari:‘musta’}),
HERTTA:Object.freeze({nimi:‘hertta’, symboli:‘\u2665’, vari:‘punainen’}),
RUUTU:Object.freeze({nimi:‘ruutu’, symboli:‘\u2666’, vari:‘punainen’})
};pakka.js:/ Pakka-luokka edustaa yhtä korttipakkaa eli 52 korttia. /

import {Maa} from “./maa.js”;
import {Kortti} from “./kortti.js”;

export class Pakka{

/ Konstruktori. Kun Pakka-luokasta luodaan olio, se saa tyhjän taulukon korteille. /
/ Tätä ei ole tarkoitus kutsua ulkopuolelta, vain luoPakka-metodin kautta. /
constructor() {
this.kortit=[];
}

/ Lisää kortin (Kortti-olion) tähän pakkaan. Tästä metodista on enemmän hyötyä aliluokassa Kasi. /
lisaaKortti(uusiKortti){
this.kortit.push(uusiKortti);
}

/ Poistaa kortin tästä pakasta ja palauttaa sen. /
otaKortti() {
return this.kortit.pop();
}

/ Sekoittaa pakan eli laittaa kortit satunnaiseen järjestykseen. */
sekoita() {
if(this.kortit.length<2) {
return;
}
else {
for(let i=0; i<this.kortit.length; i++){
let indA=Math.floor(Math.random()*this.kortit.length);
let indB=Math.floor(Math.random()this.kortit.length);
[this.kortit[indA], this.kortit[indB]]=[this.kortit[indB],this.kortit[indA]];
}
}
}

/ Staattinen metodi eli sitä kutsutaan luokan kautta: Pakka.luoPakka(); /
/ Palauttaa uuden Pakka-olion, jossa on 52 korttia. Ei voi kutsua olioissa. /
static luoPakka() {
let apupakka=new Pakka();
for(let i=1; i<=13; i++) {
apupakka.lisaaKortti(new Kortti(Maa.HERTTA, i));
apupakka.lisaaKortti(new Kortti(Maa.RUUTU, i));
apupakka.lisaaKortti(new Kortti(Maa.PATA, i));
apupakka.lisaaKortti(new Kortti(Maa.RISTI,i));
}
return apupakka;
}

/ Palauttaa pakan tiedot merkkijonona. Ei hyödyllinen graafista käyttöliittymää tehdessä. */
toString() {
return this.kortit.map(Kortti=>Kortti.toString()).join(', ');
}
};

import { Pakka } from "./moduulit/pakka.js";
import { Kasi } from "./moduulit/kasi.js";
import { Maa } from "./moduulit/maa.js";

const pelaajankasi = Kasi.luoKasi();
const jakajankasi = Kasi.luoKasi();
const pakka = Pakka.luoPakka();

const pelaajaElementit = document.querySelectorAll("#pelaajan-kortit > .kortti");
const jakajaElementit = document.querySelectorAll("#jakajan-kortit > .kortti");
const tulos = document.getElementById("tulos");
const otaKorttiButton = document.getElementById("ota-kortti");
const jaaButton = document.getElementById("jaa");
const uusiPeliButton = document.getElementById("uusi-peli");

function paivitaKortit(elementit, kortit) {
  for (let i = 0; i < elementit.length; i++) {
    if (kortit[i]) {
      elementit[i].innerHTML = kortit[i].toString();
      if (kortit[i].maa.vari === "punainen") {
        elementit[i].classList.add("punainen");
        elementit[i].classList.remove("musta");
      } else {
        elementit[i].classList.add("musta");
        elementit[i].classList.remove("punainen");
      }
    } else {
      elementit[i].innerHTML = "";
      elementit[i].classList.remove("musta");
      elementit[i].classList.remove("punainen");
    }
  }
}

function paivitaTulos() {
  tulos.innerHTML = `Pelaajan summa: ${pelaajankasi.summa} | Jakajan summa: ${jakajankasi.summa}`;
}

function aloitaPeli() {
  pakka.sekoita();
  pelaajankasi.lisaaKortti(pakka.otaKortti());
  jakajankasi.lisaaKortti(pakka.otaKortti());
  paivitaKortit(pelaajaElementit, pelaajankasi.kortit);
  paivitaKortit(jakajaElementit, [jakajankasi.kortit[0], null]);
  paivitaTulos();
}

function jakajaVuoro() {
  while (jakajankasi.summa < 17) {
    jakajankasi.lisaaKortti(pakka.otaKortti());
  }
  paivitaKortit(jakajaElementit, jakajankasi.kortit);
  paivitaTulos();
  if (jakajankasi.summa > 21 || pelaajankasi.summa > jakajankasi.summa) {
    tulos.innerHTML = "Pelaaja voitti!";
  } else if (pelaajankasi.summa < jakajankasi.summa) {
    tulos.innerHTML = "Jakaja voitti!";
  } else {
    tulos.innerHTML = "Tasapeli!";
  }
  otaKorttiButton.disabled = true;
  jaaButton.disabled = true;
  uusiPeliButton.disabled = false;
}

otaKorttiButton.addEventListener("click", () => {
  pelaajankasi.lisaaKortti(pakka.otaKortti());
  paivitaKortit(pelaajaElementit, pelaajankasi.kortit);
  paivitaTulos();
  if (pelaajankasi.summa > 21) {
    tulos.innerHTML = "Jakaja voitti!";
    otaKorttiButton.disabled = true;
    jaaButton.disabled = true;
    uusiPeliButton.disabled = false;
  }
});

jaaButton.addEventListener("click", () => {
  paivitaKortit(jakajaElementit, jakajankasi.kortit);
  jakajaVuoro();
});

uusiPeliButton.addEventListener("click", () => {
  pelaajankasi.kortit = [];
  jakajankasi.kortit = [];
  aloitaPeli();
  otaKorttiButton.disabled = false;
  jaaButton.disabled = false;
  uusiPeliButton.disabled = true;
});

aloitaPeli();

