% Laplacian operator
Lap = gallery('tridiag',Nx,1,-2,1);
Lap(1,end) = 1;
Lap(end,1) = 1;
Lap = Lap / dx^2;

% Reaction function
R = @(P, I) r*P.*(1 - P/K) + a*m*P.*I.*(1 - P/K);

% Information function
F = @(P, I) D*(Lap*I) + b*P - c*I;

% Initial conditions
P0 = 10 * ones(Nx, 1);
I0 = 2 * ones(Nx, 1);
Y0 = [P0; I0];

% ODE system
odefun = @(t, Y) [R(Y(1:Nx), Y(Nx+1:end)); F(Y(1:Nx), Y(Nx+1:end))];
[tout, Yout] = ode45(odefun, t, Y0);

% Extract results and plot
Pop = Yout(:, 1:Nx);
Inf = Yout(:, Nx+1:end);

figure;
subplot(2, 1, 1);
imagesc(t, x, Pop');
xlabel('Time');
ylabel('Space');
title('Population');
colorbar;

