rng(randSeed);

% Simulation 1: Nobody isolates
function fullPandemic()
    Infected(n).idx = -1;
    for i=1:length(infected)
        j = infected(i);
        Infected(j).idx = j;
        Infected(j).infected = true;
    end
    day = 0;
    total = length(infected);
    allInfected = false;
    while ~allInfected
        allInfected = true;
        day = day+1;
        for i=1:n
            if Infected(i).infected
                % randomly infect at most one person
                randomPerson = randi(n);
                if Infected(randomPerson).infected == false
                    Infected(randomPerson).infected = true;
                    total = total +1;
                end
            end
            if total == n
                allInfected = true;
                break
            else
                allInfected = false;
            end
        end
    end
end

