% 生成正弦信号并添加高斯噪声
Fs = 1000;                      % 采样频率
t = 0:1/Fs:1-1/Fs;              % 时间向量
x = sin(2*pi*50*t) + randn(size(t));

%使用FFT计算频谱
N = length(x);                  % 信号长度
X = fft(x);                     % 进行FFT变换
P2 = abs(X/N);                  % 双边频谱
P1 = P2(1:N/2+1);               % 单边频谱
P1(2:end-1) = 2*P1(2:end-1);    % 将单边频谱翻倍（除直流分量外）

