#include <iostream>
#include <fstream>
#include <thread>
#include <mutex>
#include <condition_variable>
#include <vector>
#include <string>
#include <sstream>
#include <atomic>
#include <chrono>
#include <semaphore.h>

std::mutex m;
std::condition_variable cv;
std::string sharedMemory;
std::atomic<bool> newCommandAvailable{false};
sem_t readerSemaphore, writerSemaphore;

void handleCommand(std::string command) {
    std::istringstream ss(command);
    std::string action;
    ss >> action;
    if (action == "read") {
        std::string filename;
        ss >> filename;
        std::fstream fs(filename);
        fs.seekg(0, std::ios::end);
        size_t size = fs.tellg();
        std::string fileContent(size, ' ');
        fs.seekg(0);
        fs.read(&fileContent[0], size);
        fs.close();
        sem_wait(&readerSemaphore);
        sharedMemory = fileContent;
        cv.notify_one();
        sem_post(&readerSemaphore);
    } else if (action == "write") {
        std::string filename, data;
        ss >> filename;
        getline(ss, data);
        std::ofstream fs(filename, std::ios::app);
        fs << data;
        fs.close();
    }
}

int main() {
    sem_init(&readerSemaphore, 0, 1);
    sem_init(&writerSemaphore, 0, 1);

    while (true) {
        std::unique_lock<std::mutex> lock(m);
        cv.wait(lock, []{ return newCommandAvailable.load(); });
        std::string command = sharedMemory;
        sharedMemory.clear();
        newCommandAvailable.store(false);
        lock.unlock();
        std::thread t(handleCommand, command);
        t.detach();
        std::this_thread::sleep_for(std::chrono::milliseconds(100));

    }
    sem_destroy(&readerSemaphore);
    sem_destroy(&writerSemaphore);

    return 0;
}
```

