server.cpp

```cpp
#include <iostream>
#include <fstream>
#include <thread>
#include <string>
#include <sstream>
#include <cstring>
#include <semaphore.h>
#include <fcntl.h>
#include <sys/shm.h>
#include <sys/mman.h>
#include <unistd.h>
#include <sys/stat.h>

const char* sharedMemoryName = "/sharedMemory";
size_t sharedMemorySize = 1024;
const char* semReaderName = "/semReader";
const char* semWriterName = "/semWriter";
const char* semResponseName = "/semResponse";

void handleCommand(char* sharedMemory, sem_t* semReader, sem_t* semWriter, sem_t* semResponse) {
    std::string command(sharedMemory);
    std::istringstream ss(command);
    std::string action;
    ss >> action;
    if (action == "read") {
        std::string filename;
        ss >> filename;
        std::fstream fs(filename);
        fs.seekg(0, std::ios::end);
        size_t size = fs.tellg();
        std::string fileContent(size, ' ');
        fs.seekg(0);
        fs.read(&fileContent[0], size);
        fs.close();
        sem_wait(semReader);
        bool first = false;
        if (strlen(sharedMemory) == 0) {
            strcpy(sharedMemory, fileContent.c_str());
            first = true;
        } else {
            strcat(sharedMemory, fileContent.c_str());
        }
        if (first) {
            sem_post(semResponse);
        }
        sem_post(semReader);
    } else if (action == "write") {
        std::string filename, data;
        ss >> filename;
        getline(ss, data);
        std::ofstream fs(filename, std::ios::app);
        fs << data;
        fs.close();
    }
}

int main() {
    int fd = shm_open(sharedMemoryName, O_CREAT | O_RDWR, 0666);
    ftruncate(fd, sharedMemorySize);
    char* sharedMemory = (char*)mmap(0, sharedMemorySize, PROT_READ | PROT_WRITE, MAP_SHARED, fd, 0);
    sem_t* semReader = sem_open(semReaderName, O_CREAT, 0666, 1);
    sem_t* semWriter = sem_open(semWriterName, O_CREAT, 0666, 1);
    sem_t* semResponse = sem_open(semResponseName, O_CREAT, 0666, 0);

    while (true) {
        sem_wait(semWriter);
        if (strlen(sharedMemory) > 0) {
            std::thread t(handleCommand, sharedMemory, semReader, semWriter, semResponse);
            t.detach();
            memset(sharedMemory, 0, sharedMemorySize);
        }
        sem_post(semWriter);
        usleep(100000); // Sleep for 100 ms
    }

    munmap(sharedMemory, sharedMemorySize);
    close(fd);
    shm_unlink(sharedMemoryName);
    sem_close(semReader);
    sem_close(semWriter);
    sem_close(semResponse);
    sem_unlink(semReaderName);
    sem_unlink(semWriterName);
    sem_unlink(semResponseName);

    return 0;
}
```

