            }
            GlobalUnlock(stgmedium.hGlobal);
        }
        ReleaseStgMedium(&stgmedium);
    }
    SetLayeredWindowAttributes(GetDesktopWindow(), 0, 255, LWA_ALPHA);
    return S_OK;
}
