```
using Minio;
using System;
using System.Threading.Tasks;

namespace MinioExample
{
    class Program
    {
        static async Task Main(string[] args)
        {
            //初始化Minio客户端
            var client = new MinioClient("localhost:9000", "accesskey", "secretkey").WithSSL(); //替换成实际的配置信息

            //上传文件
            var objectName = "test.png"; //上传的文件名，可以自行更改
            var filePath = "C:\\temp\\test.png"; //本地文件路径，可以自行更改
            var bucketName = "mybucket"; //存储桶名，可以自行更改
            await client.PutObjectAsync(bucketName, objectName, filePath);

            //下载文件
            var downloadPath = "C:\\temp\\test_downloaded.png"; //下载文件保存的本地路径，可以自行更改
            await client.GetObjectAsync(bucketName, objectName, stream =>
            {
                using (var fileStream = System.IO.File.Create(downloadPath))
                {
                    stream.CopyTo(fileStream);
                }
            });

            Console.WriteLine("操作完成");
        }
    }
}
