```c
#include <stdio.h>
#include <stdlib.h>
#include <conio.h>
#include <windows.h>

void gotoxy(int x, int y){ 
    COORD coord;
    coord.X=x;
    coord.Y=y;
    SetConsoleCursorPosition(GetStdHandle(STD_OUTPUT_HANDLE), coord);
}

int main() {
    int i, j, k;
    int width = 25, height = 20;   //宽度和高度
    int headX = 10, headY = 10;   //蛇头位置
    int foodX, foodY;             //食物位置
    int score = 0;                //得分
    int speed = 100;              //速度
    int direction = 1;            //方向,1、2、3、4分别表示上下左右

    int snakeX[100] = { headX }, snakeY[100] = { headY };  //初始化蛇身
    int snakeLength = 1;           //蛇身长度

    srand((unsigned)time(NULL));
    foodX = rand() % (width-2) + 1;      //初始化食物位置
    foodY = rand() % (height-2) + 1;

    while (1) {
        gotoxy(0, 0);
        for (i = 0; i <= width+1; i++) {
            printf("#");
        }
        printf("\n");

        for (j = 1; j <= height; j++) {
            for (i = 0; i <= width+1; i++) {
                if (i == 0 || i == width+1) {
                    printf("#");
                } else {
                    if (i == headX && j == headY) {
                        printf("@");
                    } else if (i == foodX && j == foodY) {
                        printf("$");
                    } else {
                        int flag = 0;  //标记当前位置是否是蛇身
                        for (k = 0; k < snakeLength; k++) {
                            if (i == snakeX[k] && j == snakeY[k]) {
                                printf("*");
                                flag = 1;
                                break;
                            }
                        }
                        if (!flag) {
                            printf(" ");
                        }
                    }
                }
            }
            printf("\n");
        }
        for (i = 0; i <= width+1; i++) {
            printf("#");
        }
        printf("\n");

        //处理方向键输入
        if (_kbhit()) {
            char input = _getch();
            if (input == 'w' && direction != 2) {
                direction = 1;
            } else if (input == 's' && direction != 1) {
                direction = 2;
            } else if (input == 'a' && direction != 4) {
                direction = 3;
            } else if (input == 'd' && direction != 3) {
                direction = 4;
            }
        }

        //更新蛇头位置
        switch (direction) {
        case 1:  //向上
            headY--;
            break;
        case 2:  //向下
            headY++;
            break;
        case 3:  //向左
            headX--;
            break;
        case 4:  //向右
            headX++;
            break;
        }
         //判断是否吃到食物，并更新食物位置和蛇身长度
        if (headX == foodX && headY == foodY) {
            snakeLength++;
            score += 10;
            speed -= 5;
            if (speed <= 0) {
                speed = 1;
            }
            foodX = rand() % (width-2)+1;
            foodY = rand() % (height-2)+1;
        }
        //更新蛇身
        for (k = snakeLength-1; k > 0; k--) {
            snakeX[k] = snakeX[k-1];
            snakeY[k] = snakeY[k-1];
        }
        snakeX[0] = headX;
        snakeY[0] = headY;

        //判断游戏是否结束
        if (headX <= 0 || headX >= width+1 || headY <= 0 || headY >= height) {
            break;
        }
        for (k = 1; k < snakeLength; k++) {
            if (headX == snakeX[k] && headY == snakeY[k]) {
                break;
            }
        }
        if (k < snakeLength) {
            break;
        }
        Sleep(speed);  //控制游戏速度
    }

    system("cls");
    printf("游戏结束，您的得分为：%d\n", score);

    return 0;
}
```

游戏中，我们用‘#’表示边框，‘@’表示蛇头，‘*’表示蛇身，‘$’表示食物。通过‘w’‘s’‘a’‘d’控制蛇的运动方向，吃到食物后蛇身长度增加，得分和速度加快。

