        if (i == 0 || j == 0 || i == length - 1 || j == height - 1)
        {
            entireEdgeTiles[num] = true;
        }

        //设置当前Tiles数组（平铺瓦片）中的位置值和全局地图ID。
        worldGrid.allTiles[num].id = num;
