        serializedOpt = new SerializedObject(new SerializedObjectScript(opt));

        startArea = serializedOpt.FindProperty("startArea");
        endArea = serializedOpt.FindProperty("endArea");
        tiles = serializedOpt.FindProperty("tiles");
        withHole = serializedOpt.FindProperty("withHole");
        invert = serializedOpt.FindProperty("invert");
        index = serializedOpt.FindProperty("index");
    }
}
