using UnityEngine;

public class _Map_IO_opt : ISerializationCallbackReceiver
{
    public enum _Map_IO_typeofMap { Heightmap, Holemap, Alphamap, Detailmap };
    public enum _Map_IO_typeofExport { Full, Area, Tiles};
    public enum _Map_IO_typeofOutput { Png, Jpg, Tga, Txt, GameObject};

    [SerializeField] private Vector2Int startArea;
    [SerializeField] private Vector2Int endArea;
    [SerializeField] private Vector2Int tiles;
    [SerializeField] private bool withHole;
    [SerializeField] private bool invert;
    [SerializeField] private int index;

    public Vector2Int StartArea { get => startArea; set => startArea = value; }
    public Vector2Int EndArea { get => endArea; set => endArea = value; }
    public Vector2Int Tiles { get => tiles; set => tiles = value; }
    public bool WithHole { get => withHole; set => withHole = value; }
    public bool Invert { get => invert; set => invert = value; }
    public int Index { get => index; set => index = value; }

    public void OnBeforeSerialize() { }

    public void OnAfterDeserialize() { }
}
```

