CYCLIC
    // 采集当前速度
    actual_speed := get_actual_speed();

    // EKF预测
    ekf := ekf_predict(ekf);

    // EKF更新
    ekf := ekf_update(ekf, actual_speed);

    // 获取EKF前馈控制信号
    control_signal_ff := get_control_signal_from_ekf(ekf);

    // 计算误差
    error := desired_speed - actual_speed;

    // PID控制
    control_signal_fb := pid_control(pid, error, sampling_time);

    // 总控制信号是前馈和反馈控制信号的和
