export function parseLineStr(name, str, isIntLine, toNumber, maxLen, vueInstance) {
  name = name || ''
  if (type(str) !== 'string') {
    vueInstance && vueInstance.$message({
      type: 'warning',
      message: name + '请按格式输入'
    })
    return false
  }
  str = str.trim()
  var values = str.split(/[\r\n]+/)
  var reg = /^\d+$/
  var newValues = []
  for (var i = 0; i < values.length; i++) {
    var value = values[i]
    value = value.trim()
    if (value) {
      if (isIntLine) {
        if (reg.test(value)) {
          newValues.push(toNumber ? Number(value) : value); 2
        } else {
          vueInstance && vueInstance.$message({
            type: 'warning',
            message: name + '每一行必须是由数字组成'
          })
          return false
        }
      } else {
        newValues.push(value)
      }
    }
  }

  if (isNum(maxLen) && maxLen > 0 && newValues.length > maxLen) {
    vueInstance.$message({
      type: 'warning',
      message: name + '不能超过' + maxLen + '个'
    })
    return false
  }

  return newValues
}

