  var tmpUrl = url || ''
  tmpUrl = tmpUrl.toLowerCase()
  if (tmpUrl.indexOf('http') === 0) {
    var domainReg = /^http(s)?:\/\/(www\.)?([a-zA-Z0-9][-a-zA-Z0-9]{0,62}(\.[a-zA-Z0-9][-a-zA-Z0-9]{0,62})+)(:[0-9]+)?/i
    var results = url.match(domainReg)
    var httpHost, domain, dotIndex
    if (results) {
      httpHost = results[0]
      domain = results[3]
      dotIndex = domain.indexOf('.')
      return {
        name: domain.substr(0, dotIndex),
        domain: domain + (results[5] || ''), // 仅host+端口
        host: httpHost, // 包含http部分
        pathname: url.substr(httpHost.length) // 包括#?号后面的数据
      }
    } else {
      return {
        name: '',
        domain: '', // 仅host
        host: '', // 包含http部分
        pathname: url // 包括#?号后面的数据
      }
    }
  } else {
    return {
      name: '',
      domain: '', // 仅host
      host: '', // 包含http部分
      pathname: url // 包括#?号后面的数据
    }
  }
}

import { parseUrl } from './helperFunctions'

describe('parseUrl', () => {
  it('should return an object with empty values when no URL is provided', () => {
    expect(parseUrl()).toEqual({
      name: '',
      domain: '',
      host: '',
      pathname: ''
    })
  })

  it('should return an object with domain and host values when a valid http URL is provided', () => {
    const url = 'http://www.example.com/path/to/page.html'
    const result = parseUrl(url)
    expect(result.domain).toBe('example.com')
    expect(result.host).toBe('http://www.example.com')
  })

  it('should correctly parse URLs with subdomains', () => {
    const url = 'https://www.subdomain.example.co.uk'
    const result = parseUrl(url)
    expect(result.domain).toBe('subdomain.example.co.uk')
    expect(result.host).toBe('https://www.subdomain.example.co.uk')
  })

  it('should correctly parse URLs with a port number', () => {
    const url = 'http://example.com:8080'
    const result = parseUrl(url)
    expect(result.domain).toBe('example.com:8080')
    expect(result.host).toBe('http://example.com:8080')
  })

  it('should return an object with empty name value when no domain is present in the URL', () => {
    const url = 'http://localhost'
    const result = parseUrl(url)
    expect(result.name).toBe('')
  })

  it('should return an object with empty host value when no URL scheme is present', () => {
    const url = 'www.example.com'
    const result = parseUrl(url)
    expect(result.host).toBe('')
  })

  it('should return the entire URL path in the pathname property', () => {
    const url = 'https://www.example.com/path/to/page.html?query=string#hash'
    const result = parseUrl(url)
    expect(result.pathname).toBe('/path/to/page.html?query=string#hash')
  })
})

