import java.util.stream.Collectors;

/**
 * @description:(更新合伙人人数时，需要刷新高端额度)
 * @return
 * @author: haiguang.chen
 * @date: 2023/4/21 9:12
 * @since JDK 1.8
 */
@Service("partnersNumberUpdateFacade")
public class PartnersNumberUpdateFacadeService  implements PartnersNumberUpdateFacade {

    private static final Logger logger = LoggerFactory.getLogger(PartnersNumberUpdateFacadeService.class);
    @Autowired
    private HighProductTradeLimitService highProductTradeLimitService;
    @Autowired
    private QuotaCacheRefreshService quotaCacheRefreshService;
    @Autowired
    private UpdateCurrentLimitOrNumService updateCurrentLimitOrNumService;
    @Autowired
    private HighProductService highProductService;

    /**
     * 最小的合伙人数
     */
    private static final Integer MIN_PARTNERS_NUM = 1;


    /**
     * @description:(处理)
     * @param request
     * @return com.howbuy.tms.high.batch.facade.trade.partnersnumberupdate.PartnersNumberUpdateResponse
     * @author: haiguang.chen
     * @date: 2023/4/21 9:14
     * @since JDK 1.8
     */
    @Override
    public PartnersNumberUpdateResponse execute(PartnersNumberUpdateRequest request) {
        PartnersNumberUpdateResponse response = new PartnersNumberUpdateResponse();
        response.setReturnCode(ExceptionCodes.SUCCESS);
        response.setDescription(MessageSource.getMessageByCode(ExceptionCodes.SUCCESS));

        if(MIN_PARTNERS_NUM.compareTo(request.getPartnersNumber()) > 0){
            response.setDescription("合伙人人数配置错误");
            response.setReturnCode(ExceptionCodes.PARAMS_ERROR);
            return response;
        }

        // 获取额度明细表该客户已记录的产品列表
        List<String> fundCodeList = highProductTradeLimitService.getLimitDtlAllFundCodeByTxAcctNo(request.getTxAcctNo());
        if(CollectionUtils.isEmpty(fundCodeList)){
            response.setDescription("该客户在额度表不存在");
            return response;
        }

        // 更新额度明细表
        int num = highProductTradeLimitService.updatePartnerNumByTxAcctNo(request.getTxAcctNo(),request.getPartnersNumber());
        if(num <= 0){
            response.setDescription("更新额度明细表失败");
            response.setReturnCode(ExceptionCodes.PARAMS_ERROR);
            return response;
        }

        // 刷新额度缓存
        quotaCacheRefreshService.refresh(fundCodeList);

        // 刷新销控额度
        updateCurrentLimitOrNumService.processTask(fundCodeList);

        // 查询产品总额度，比较是否超限
        List<HighProductBaseInfoModel> highProductBaseInfoModels = highProductService.getHighProductBaseInfo(fundCodeList);
        if(CollectionUtils.isEmpty(highProductBaseInfoModels)){
            response.setDescription("获取产品信息失败");
            response.setReturnCode(ExceptionCodes.PARAMS_ERROR);
            return response;
        }

        Map<String, HighProductBaseInfoModel> highProductBaseInfoModelMap = highProductBaseInfoModels.stream()
                .collect(Collectors.toMap(HighProductBaseInfoModel::getFundCode, Function.identity()));

        StringBuilder alarmMsg = new StringBuilder();
        for(String fundCode : fundCodeList){
            HighProductBaseInfoModel model = highProductBaseInfoModelMap.get(fundCode);
            if(model == null){
                logger.error("产品{}获取不到基本信息",fundCode);
                continue;
            }

            HighProductSaleVo highQuotaBean = highProductTradeLimitService.getProductSaleInfo(fundCode);
            BigDecimal usedAmt = BigDecimalUtil.addBigDecimal(highQuotaBean.getLockUsedAmount(), highQuotaBean.getUnLockUsedAmount());
            if(model.getIpoAmountLimit() != null) {
                BigDecimal leftTotalAmount = model.getIpoAmountLimit().subtract(usedAmt);
                if (leftTotalAmount.compareTo(BigDecimal.ZERO) < 0) {
                    logger.error("产品{}额度不足", fundCode);
                    // 产品：代码-简称，人数超限
                    alarmMsg.append(fundCode).append("-").append(model.getFundAttr()).append("，人数超限;");
                }
            }
        }

        response.setAlarmMsg(alarmMsg.toString());

        return response;
    }
}

