I have provided a possible implementation of a simple TCP server in C for this road transport office purpose. This implementation assumes that you have the necessary database (SQLite used in this example) and the SQL commands are adjusted accordingly. Install sqlite3 in your system and include the necessary libraries.

```c
#include <arpa/inet.h>
#include <netinet/in.h>
#include <sqlite3.h>
#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <sys/socket.h>
#include <time.h>
#include <unistd.h>

#define PORT 8080
#define MAX 1024

void calculate_fine(char *vehicle_number, int *fine);
void process_request(int client_socket);
void send_response(int client_socket, char *response);

int main() {
    int server_socket, client_socket;
    struct sockaddr_in serv_addr, cli_addr;
    socklen_t addr_len = sizeof(struct sockaddr_in);

    server_socket = socket(AF_INET, SOCK_STREAM, 0);
    if (server_socket == 0) {
        perror("Socket creation error");
        exit(EXIT_FAILURE);
    }

    memset(&serv_addr, 0, addr_len);
    serv_addr.sin_family = AF_INET;
    serv_addr.sin_addr.s_addr = INADDR_ANY;
    serv_addr.sin_port = htons(PORT);

    if (bind(server_socket, (struct sockaddr *)&serv_addr, addr_len) < 0) {
        perror("Bind error");
        exit(EXIT_FAILURE);
    }

    if (listen(server_socket, 5) < 0) {
        perror("Listen error");
        exit(EXIT_FAILURE);
    }

    while (1) {
        client_socket =
            accept(server_socket, (struct sockaddr *)&cli_addr, &addr_len);
        if (client_socket < 0) {
            perror("Accept error");
            continue;
        }

        process_request(client_socket);
    }
}

void process_request(int client_socket) {
    char buffer[MAX];
    char response[MAX];
    bzero(buffer, MAX);

    read(client_socket, buffer, sizeof(buffer));

    if (strlen(buffer) != 10) {
        strcpy(response, "Invalid vehicle number");
    } else {
        int fine = 0;
        calculate_fine(buffer, &fine);
        snprintf(response, sizeof(response), "Fine amount: Rs. %d", fine);
    }
    send_response(client_socket, response);
}

void calculate_fine(char *vehicle_number, int *fine) {
    sqlite3 *db;
    sqlite3_stmt *stmt;
    const char *tail;
    int rc;

    rc = sqlite3_open("road_transport.db", &db);

    if (rc) {
        fprintf(stderr, "Can't open database: %s\n", sqlite3_errmsg(db));
        exit(EXIT_FAILURE);
    }

    char query[MAX];
    snprintf(query, sizeof(query),
             "SELECT road_tax_paid, traffic_offences FROM vehicles WHERE "
             "registration_number='%s';",
             vehicle_number);

    rc = sqlite3_prepare_v2(db, query, strlen(query), &stmt, &tail);

    if (rc != SQLITE_OK) {
        fprintf(stderr, "SQL error: %s\n", sqlite3_errmsg(db));
        exit(EXIT_FAILURE);
    }

    int road_tax_paid = 0;
    int traffic_offences = 0;
    if (sqlite3_step(stmt) == SQLITE_ROW) {
        road_tax_paid = sqlite3_column_int(stmt, 0);
        traffic_offences = sqlite3_column_int(stmt, 1);
    }

    sqlite3_finalize(stmt);
    sqlite3_close(db);

    *fine = 500 * traffic_offences;
    if (!road_tax_paid) {
        *fine += 1000;
    }
    if (strncmp(vehicle_number, "TN", 2) != 0) {
        *fine += 2400;
    }
}

void send_response(int client_socket, char *response) {
    write(client_socket, response, strlen(response));
    close(client_socket);
}
```

