  // Находим форму поиска и список результатов
  var form = document.querySelector(‘form’);
  var resultsList = document.querySelector(‘#search-results’);

  // Обрабатываем отправку формы
  form.addEventListener(‘submit’, function(event) {
    // Отменяем действие по умолчанию (отправку формы)
    event.preventDefault();

    // Получаем значение из поля ввода (запрос пользователя)
    var query = form.querySelector(‘#search-input’).value.trim();

    // Если запрос не пустой, запускаем поиск
    if (query) {
      // Обнуляем список результатов
      resultsList.innerHTML = ‘’;

      // Выполняем поиск по базе данных и получаем массив результатов
      var resultsArray = searchDatabase(query);

      // Если результаты найдены, выводим их на страницу
      if (resultsArray.length) {
        resultsArray.forEach(function(result) {
          // Создаем элемент списка и добавляем его на страницу
          var item = document.createElement(‘li’);
          item.textContent = result;
          resultsList.appendChild(item);
        });
      } else {
        // Если результаты не найдены, выводим сообщение
        var message = document.createElement(‘p’);
        message.textContent = ‘Ничего не найдено’;
        resultsList.appendChild(message);
      }
    }
  });

  // Функция поиска по базе данных
  function searchDatabase(query) {
    var results = [];  // Массив результатов

    // Читаем файл базы данных
    var xhr = new XMLHttpRequest();
    xhr.open(‘GET’, ‘database.json’, false);
    xhr.send();
    if (xhr.status === 200) {
      var data = JSON.parse(xhr.responseText);

      // Проходим по каждой записи в базе данных
      data.forEach(function(item) {
        // Выполняем поиск по заголовку и описанию записи
        if (item.title.indexOf(query) !== -1 || item.description.indexOf(query) !== -1) {
          // Если запрос найден, добавляем заголовок записи в массив результатов
          results.push(item.title);
        }
      });
    }

    return results;
  }
