async function addUnitsToCamping(camping, units, res) {
  const session = await mongoose.startSession().startTransaction();
  const campingUnits = units.map(unit => {
    if (unit._id.startsWith('new')) {
      delete unit._id;
    }
    const campingUnit = new CampingUnit(unit);
    campingUnit.camping = camping._id;
    return campingUnit.validate().then(() => campingUnit);
  });
  try {
    await CampingUnit.insertMany(await Promise.all(campingUnits), { session });
    await session.commitTransaction();
  } catch (error) {
    await session.abortTransaction();
    return res.status(500).json({
      error: 'invalid_unit',
      errors: error.errors,
    });
  } finally {
    session.endSession();
  }
} 

