                sql = ((org.hibernate.jpa.Query) ((org.springframework.data.jpa.repository.query.AbstractStringBasedJpaQuery) ((org.springframework.data.jpa.repository.query.JpaQueryMethod) ((org.springframework.data.jpa.repository.query.AbstractJpaQuery) ((org.springframework.data.jpa.repository.query.DefaultJpaQueryMethodInvoker) joinPoint.getThis()).getQueryMethod()).getQuery()).getQuery()).getHibernateQuery().getQueryString();
            } else {
